import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import logo from "./assets/TeamC.png";
import AuthService from "./services/auth-service";

import Home from "./components/home-component";
import FooterComponent from "./components/footer-component";
import Login from "./components/login-component";
import Register from "./components/register-component";
import Profile from "./components/profile-component";
import BoardUser from "./components/board-user-component";
import BoardHr from "./components/board-hr-component";
import BoardAdmin from "./components/board-admin-component";
import CreateEmployeeComponent from "./components/employee-create-component";
import UpdateEmployeeComponent from "./components/employee-update-component";
import ViewEmployeeComponent from "./components/employee-view-component";
import UserUpdateComponent from "./components/user-update-component";
import ViewDeletedEmployee from "./components/view-deleted-component";
import ForgotPassword from "./components/forgot-password-form";
import ResetPassword from "./components/reset-password-form";
import ChangePassword from "./components/profile-change-password";
import Skillset from "./components/skillset-component";
import SkillsetList from "./components/skillset-list";
import CreateSkillset from "./components/skillset-add";

class App extends Component {
  constructor(props) {
    super(props);
    this.logOut = this.logOut.bind(this);

    this.state = {
      showUserBoard: false,
      showHrBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    };
  }

  componentDidMount() {
    const user = AuthService.getCurrentUser();

    if (user) {
      this.setState({
        currentUser: user,
        showUserBoard: user.roles.includes("ROLE_USER"),
        showHrBoard: user.roles.includes("ROLE_HR"),
        showAdminBoard: user.roles.includes("ROLE_ADMIN"),
      });
    }
  }

  logOut() {
    AuthService.logout();
  }

  render() {
    const {
      currentUser,
      showUserBoard,
      showHrBoard,
      showAdminBoard,
    } = this.state;

    return (
      <div className="main-app-container">
        <Router>
          <nav className="navbar navbar-expand navbar-dark bg-dark">
            <Link to={"/home"} className="navbar-brand">
              <img src={logo} width="25" height="25" alt="" />
              <span> </span>
              <strong>Employee Management System</strong>
            </Link>

            <div className="navbar-nav mr-auto">
              {showHrBoard && (
                <div>
                  <li className="nav-item">
                    <Link to={"/hr/employee-list"} className="nav-link">
                      HR Board <i className="fa fa-clipboard"></i>
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link to={"/register"} className="nav-link">
                      Register New Account <i className="fa fa-user-plus"></i>
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link to={"/skillset-list"} className="nav-link">
                      Skillset List <i className="fa fa-tools"></i>
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link to={"/add-skillset"} className="nav-link">
                      Add Skillset <i className="fa fa-tools"></i>
                    </Link>
                  </li>
                </div>
              )}

              {showAdminBoard && (
                <li className="nav-item">
                  <Link to={"/admin"} className="nav-link">
                    Admin Board <i className="fa fa-clipboard"></i>
                  </Link>
                </li>
              )}

              {showUserBoard && (
                <div>
                  <li className="nav-item">
                    <Link to={"/user"} className="nav-link">
                      User Board <i className="fa fa-clipboard"></i>
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link to={"/skillset"} className="nav-link">
                      Skillset <i className="fa fa-tools"></i>
                    </Link>
                  </li>
                </div>
              )}
            </div>

            {currentUser ? (
              <div className="navbar-nav ml-auto">
                <li className="nav-item">
                  <Link to={"/profile"} className="nav-link">
                    {currentUser.username} <i className="fa fa-user"></i>
                  </Link>
                </li>
                <li className="nav-item">
                  <a href="/login" className="nav-link" onClick={this.logOut}>
                    Log Out <i className="fa fa-sign-out"></i>
                  </a>
                </li>
              </div>
            ) : (
              <div className="navbar-nav ml-auto">
                <li className="nav-item">
                  <Link to={"/login"} className="nav-link">
                    Login <i className="fa fa-sign-in"></i>
                  </Link>
                </li>
              </div>
            )}
          </nav>

          <div className="container mt-3">
            <Switch>
              <Route exact path={["/", "/home"]} component={Home} />
              <Route exact path="/login" component={Login} />
              <Route exact path="/register" component={Register} />
              <Route exact path="/profile" component={Profile} />
              <Route path="/user" component={BoardUser} />
              <Route path="/hr/employee-list" component={BoardHr}></Route>
              <Route path="/admin" component={BoardAdmin} />
              <Route path="/skillset" component={Skillset} />
              <Route path="/skillset-list" component={SkillsetList} />
              <Route path="/add-skillset" component={CreateSkillset} />

              <Route
                path="/user-update/:id"
                component={UserUpdateComponent}
              ></Route>

              <Route
                path="/add-employee"
                component={CreateEmployeeComponent}
              ></Route>

              <Route
                path="/view-employee/:id"
                component={ViewEmployeeComponent}
              ></Route>

              <Route
                path="/update-employee/:id"
                component={UpdateEmployeeComponent}
              ></Route>

              <Route
                path="/view-deleted-employee"
                component={ViewDeletedEmployee}
              ></Route>

              <Route path="/forgot_password" component={ForgotPassword}></Route>

              <Route
                path="/reset_password/:token"
                component={ResetPassword}
              ></Route>

              <Route path="/change_password" component={ChangePassword}></Route>
            </Switch>
          </div>
          <FooterComponent />
        </Router>
      </div>
    );
  }
}

export default App;
