import React from "react";
import Likert from "react-likert-scale";

/* const likertOptions = {
  question: "Proficiency level",
  responses: [
    { value: 1, text: "0" },
    { value: 2, text: "1" },
    { value: 3, text: "2" },
    { value: 4, text: "3" },
    { value: 5, text: "4" },
  ],
  picked: (val) => {
    console.log(val);
  },
}; */

const AddSkillset = (props) => {
  return props.skillDetails.map((val, idx) => {
    let skill = `skill-${idx}`,
      category = `category-${idx}`,
      yearsOfExperience = `yearsOfExperience-${idx}`,
      proficiencyLevel = `proficiencyLevel-${idx}`;

    /* const likertOptions = {
      question: "",
      responses: [
        { value: 1, text: "1" },
        { value: 2, text: "2" },
        { value: 3, text: "3" },
        { value: 4, text: "4" },
        { value: 5, text: "5" },
        { value: 6, text: "6" },
      ],
      picked: (val) => {
        console.log(val);
      },
    }; */

    return (
      <div className="form-row" key={val.index}>
        <div className="col">
          <label>Name</label>
          <input
            type="text"
            className="form-control required"
            placeholder="Skill"
            name="skill"
            data-id={idx}
            id={skill}
          />
        </div>

        <div className="col">
          <label>Category</label>
          <select
            className="form-control"
            name="category"
            id={category}
            data-id={idx}
          >
            <option>Select</option>
            <option>Soft Skill</option>
            <option>Domain Knowledge</option>
            <option>Technical Skill</option>
          </select>
        </div>

        <div className="col">
          <label>Experience</label>
          <input
            type="number"
            className="form-control"
            placeholder="Years of experience"
            name="yearsOfExperience"
            id={yearsOfExperience}
            data-id={idx}
            maxLength="2"
            min="0"
            max="99"
          />
        </div>
        <div className="col">
          <label>Proficiency Level</label>
          <input
            type="text"
            className="form-control"
            placeholder="1 - 6"
            name="proficiencyLevel"
            id={proficiencyLevel}
            data-id={idx}
          />
          {/* <Likert
            name="proficiencyLevel"
            id={proficiencyLevel}
            data-id={idx}
            {...likertOptions}
          /> */}
        </div>
        <div className="col p-4">
          {idx === 0 ? (
            <button
              onClick={() => props.add()}
              type="button"
              className="btn btn-primary text-center"
            >
              <i className="fa fa-plus-circle" aria-hidden="true" />
            </button>
          ) : (
            <button
              className="btn btn-danger"
              onClick={() => props.delete(val)}
            >
              <i className="fa fa-minus" aria-hidden="true" />
            </button>
          )}
        </div>
      </div>
    );
  });
};

export default AddSkillset;
