import React, { Component } from "react";
import UserService from "../services/UserService";
import { storage } from "../firebase";
import documents from "../assets/documents.png";
import Form from "react-validation/build/form";
import CheckButton from "react-validation/build/button";
import { isEmail } from "validator";

import ReactHover from "react-hover";
import { Trigger, Hover } from "react-hover/dist/ReactHover";

const email = (value) => {
  if (!isEmail(value)) {
    return (
      <div className="alert alert-danger" role="alert">
        This is not a valid email.
      </div>
    );
  }
};

class UserUpdateComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: this.props.match.params.id,
      employeeId: "",
      firstName: "",
      lastName: "",
      personalEmail: "",
      mailingAddress: "",
      dateOfBirth: "",
      department: "",
      position: "",
      reportingSupervisor: "",
      dateJoined: "",
      experience: "",
      contact: "",
      officeLocation: "",
      profilePicture: "",
      attachment: "",
      fileList: [],
      successful: false,
      message: "",
    };
    this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
    this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
    this.updateUser = this.updateUser.bind(this);
  }

  componentDidMount() {
    UserService.getUserBoard(this.state.id).then((res) => {
      let employee = res.data;
      this.setState({
        employeeId: employee.employeeId,
        firstName: employee.firstName,
        lastName: employee.lastName,
        personalEmail: employee.personalEmail,
        mailingAddress: employee.mailingAddress,
        dateOfBirth: employee.dateOfBirth,
        department: employee.department,
        position: employee.position,
        reportingSupervisor: employee.reportingSupervisor,
        dateJoined: employee.dateJoined,
        experience: employee.experience,
        contact: employee.contact,
        officeLocation: employee.officeLocation,
        profilePicture: employee.profilePicture,
        attachment: employee.attachment,
      });
    });
  }

  updateUser = (e) => {
    e.preventDefault();

    this.setState({
      successful: false,
      message: "",
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      let employee = {
        employeeId: this.state.employeeId,
        firstName: this.state.firstName,
        lastName: this.state.lastName,
        personalEmail: this.state.personalEmail,
        mailingAddress: this.state.mailingAddress,
        dateOfBirth: this.state.dateOfBirth,
        department: this.state.department,
        position: this.state.position,
        reportingSupervisor: this.state.reportingSupervisor,
        dateJoined: this.state.dateJoined,
        experience: this.state.experience,
        contact: this.state.contact,
        officeLocation: this.state.officeLocation,
        profilePicture: this.state.profilePicture,
        attachment: this.state.attachment,
        fileList: this.state.fileList,
      };

      // console test view
      console.log("employee => " + JSON.stringify(employee));

      UserService.updateUser(this.state.id, employee).then(
        (res) => {
          this.setState({
            successful: true,
            message: res.data.message,
          });
          this.props.history.push(`/user/${this.state.id}`);
        },
        (error) => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            successful: false,
            message: resMessage,
          });
        }
      );
    }
  };

  changeEmployeeIdHandler = (event) => {
    this.setState({ employeeId: event.target.value });
  };

  changeFirstNameHandler = (event) => {
    this.setState({ firstName: event.target.value });
  };

  changeLastNameHandler = (event) => {
    this.setState({ lastName: event.target.value });
  };

  changeEmailHandler = (event) => {
    this.setState({ personalEmail: event.target.value });
  };

  changeAddressHandler = (event) => {
    this.setState({ mailingAddress: event.target.value });
  };

  changeDOBHandler = (event) => {
    this.setState({ dateOfBirth: event.target.value });
  };

  changeDepartmentHandler = (event) => {
    this.setState({ department: event.target.value });
  };

  changePositionHandler = (event) => {
    this.setState({ position: event.target.value });
  };

  changeReportingSupervisorHandler = (event) => {
    this.setState({ reportingSupervisor: event.target.value });
  };

  changeDateJoinedHandler = (event) => {
    this.setState({ dateJoined: event.target.value });
  };

  changeExperienceHandler = (event) => {
    this.setState({ experience: event.target.value });
  };

  changeContactHandler = (event) => {
    this.setState({ contact: event.target.value });
  };

  changeOfficeLocationHandler = (event) => {
    this.setState({ officeLocation: event.target.value });
  };

  changeProfilePictureHandler = (event) => {
    this.setState({ profilePicture: event.target.value });
  };

  changeAttachmentHandler = (event) => {
    this.setState({ attachment: event.target.value });
  };

  cancel() {
    this.props.history.push(`/user/${this.state.id}`);
  }

  getAttachment() {
    const filePreviewOption = {
      followCursor: false,
      shiftX: 20,
      shiftY: 0,
    };

    if (this.state.id === "add") {
      return;
    } else {
      if (!this.state.attachment) {
        return;
      } else {
        const downloadLink = [];
        for (const [index, value] of this.state.attachment
          .split(",")
          .entries()) {
          const initial = value
            .split(RegExp("%2..*%2F(.*?)alt"))[1]
            .split(".")[0];
          const fileName = initial.replaceAll("%20", " ");
          //downloadLink.push(<li key={index}><a href={value}>{fileName</a></li>)
          downloadLink.push(
            <ReactHover options={filePreviewOption}>
              <Trigger type="trigger">
                <ul className="list-group list-group-flush">
                  <li
                    className="list-group-item"
                    style={{ marginBottom: "5px" }}
                    key={index}
                  >
                    <a href={value}>{fileName}</a>
                  </li>
                </ul>
              </Trigger>
              <Hover type="hover">
                <div>
                  <iframe
                    src={value}
                    title="File Preview"
                    style={{ width: "500px", height: "300px" }}
                  />
                </div>
              </Hover>
            </ReactHover>
          );
        }
        return downloadLink;
      }
    }
  }

  render() {
    const uploadImage = (e) => {
      const image = e.target.files[0];
      const uploadTask = storage.ref(`images/${image.name}`).put(image);
      uploadTask.on(
        "state_changed",
        (snapshot) => {
          // progress function ....
          const progress = Math.round(
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100
          );
          this.setState({ progress });
        },
        (error) => {
          // error function ....
          console.log(error);
        },
        () => {
          // complete function ....
          storage
            .ref("images")
            .child(image.name)
            .getDownloadURL()
            .then((url) => {
              console.log(url);
              this.setState({ profilePicture: url });
            });
        }
      );
    };

    const uploadAttachment = (e) => {
      if (this.state.employeeId === "") {
        alert("Wrong ID");
        return;
      } else {
        console.log("file length: ", e.target.files.length);

        for (let i = 0; i < e.target.files.length; i++) {
          const file = e.target.files[i];
          try {
            if (file.size > 5242880) {
              alert("File size is too big! Please choose another file.");
            } else {
              const storageRef = storage.ref(
                `attachments/` + this.state.employeeId
              );
              const fileRef = storageRef.child(file.name);

              fileRef.put(file).then(() => {
                console.log("Uploaded " + file.name + " successfully");
              });
            }
          } catch {
            return;
          }
        }
        try {
          setTimeout(() => {
            const storageRef = storage.ref(
              `attachments/` + this.state.employeeId
            );
            storageRef
              .listAll()
              .then((res) => {
                res.items.forEach((itemRef) => {
                  itemRef
                    .getDownloadURL()
                    .then((url) => {
                      var attachmentList = this.state.fileList.concat(url);
                      this.setState({ fileList: attachmentList });
                      this.setState({
                        attachment: this.state.fileList.join(","),
                      });
                      console.log(this.state.fileList);
                      console.log(this.state.attachment);
                    })
                    .catch(function (error) {
                      console.log(error);
                    });
                }, 2000);
              })
              .catch(function (error) {
                console.log(error);
              });
          });
        } catch {
          return;
        }
      }
    };

    return (
      <div className="container">
        <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12 text-center">
          <h2 className="text">Employee Update Form</h2>
        </div>
        <div className="row gutters" style={{ marginTop: "20px" }}>
          <div className="col-xl-3 col-lg-3 col-md-12 col-sm-12 col-12">
            <div className="card h-100">
              <div className="card-body text-center">
                <div className="view-account">
                  <div className="view">
                    <div className="form-group"></div>
                    <h5 className="name">
                      {this.state.firstName} {this.state.lastName}'s Profile
                      Picture
                    </h5>
                    {/* <p>
                      {this.state.employeeId} <span> </span>
                      <span>
                        <i className="fa fa-user"></i>
                      </span>
                    </p> */}
                    <div className="image">
                      <img
                        src={
                          this.state.profilePicture ||
                          "http://via.placeholder.com/150x150"
                        }
                        alt="Profile_Picture"
                        height="150"
                        width="150"
                        className=" rounded-circle mx-auto d-block"
                      />
                      {/* <progress value={this.state.progress} max="100" /> */}
                      <br />
                      <input type="file" onChange={uploadImage} />
                      {/* <button onClick={this.handleUpload}>Upload</button> */}
                      <br />
                    </div>
                    <br />
                    <hr />
                    <h5 className="name">Attachment</h5>
                    <div className="text-center">
                      <div className="image">
                        <img
                          src={documents}
                          alt="Profile_Attachment"
                          height="150"
                          width="150"
                          className=" rounded-circle mx-auto d-block"
                        />
                        <br />
                        <label className="file">
                          <input
                            type="file"
                            multiple
                            onChange={uploadAttachment}
                          />
                        </label>

                        <div className="card-header">List of Files</div>
                        {this.getAttachment()}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="col-xl-9 col-lg-9 col-md-12 col-sm-12 col-12">
            <div className="card h-100">
              <Form
                onSubmit={this.updateUser}
                ref={(c) => {
                  this.form = c;
                }}
              >
                {!this.state.successful && (
                  <div>
                    <div className="card-body">
                      <div className="row gutters">
                        <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
                          <h6 className="mb-3 text-primary">
                            Personal Details
                          </h6>
                        </div>
                        <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                          <label>First Name:</label>
                          <div
                            className="form-control text-white"
                            id="disabledInput"
                            style={{ background: "#dddddd" }}
                          >
                            <label>{this.state.firstName}</label>
                          </div>
                        </div>
                        <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                          <label>Last Name:</label>
                          <div
                            className="form-control text-white"
                            id="disabledInput"
                            style={{ background: "#dddddd" }}
                          >
                            <label>{this.state.lastName}</label>
                          </div>
                        </div>
                        <div
                          className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                          style={{ marginTop: "15px" }}
                        >
                          <label>Date of Birth:</label>
                          <div
                            className="form-control text-white"
                            id="disabledInput"
                            style={{ background: "#dddddd" }}
                          >
                            <label>{this.state.dateOfBirth}</label>
                          </div>
                        </div>
                        <div
                          className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                          style={{ marginTop: "15px" }}
                        >
                          <label>Personal Email:</label>
                          <input
                            placeholder="Email Address"
                            name="personalEmail"
                            className="form-control"
                            value={this.state.personalEmail}
                            onChange={this.changeEmailHandler}
                            validations={[email]}
                          />
                        </div>
                        <div
                          className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12"
                          style={{ marginTop: "15px" }}
                        >
                          <label>Mailing Address:</label>
                          <textarea
                            placeholder="Mailing Address"
                            rows="4"
                            name="mailing addreess"
                            className="form-control"
                            value={this.state.mailingAddress}
                            onChange={this.changeAddressHandler}
                          />
                        </div>
                      </div>
                      <div
                        className="row gutters"
                        style={{ marginTop: "30px" }}
                      >
                        <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
                          <h6 className="mb-3 text-primary">
                            Employement Details
                          </h6>
                        </div>
                        <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                          <label>Employee ID:</label>
                          <div
                            className="form-control text-white"
                            id="disabledInput"
                            style={{ background: "#dddddd" }}
                          >
                            <label>{this.state.employeeId}</label>
                          </div>
                        </div>
                        <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                          <label>Contact:</label>
                          <input
                            placeholder="contact"
                            name="contact"
                            className="form-control"
                            value={this.state.contact}
                            onChange={this.changeContactHandler}
                          />
                        </div>

                        <div
                          className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                          style={{ marginTop: "15px" }}
                        >
                          <label>Department:</label>
                          <div
                            className="form-control text-white"
                            id="disabledInput"
                            style={{ background: "#dddddd" }}
                          >
                            <label>{this.state.department}</label>
                          </div>
                        </div>
                        <div
                          className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                          style={{ marginTop: "15px" }}
                        >
                          <label>Position:</label>
                          <div
                            className="form-control text-white"
                            id="disabledInput"
                            style={{ background: "#dddddd" }}
                          >
                            <label>{this.state.position}</label>
                          </div>
                        </div>
                        <div
                          className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                          style={{ marginTop: "15px" }}
                        >
                          <label>Reporting Supervisor:</label>
                          <div
                            className="form-control text-white"
                            id="disabledInput"
                            style={{ background: "#dddddd" }}
                          >
                            <label>{this.state.reportingSupervisor}</label>
                          </div>
                        </div>
                        <div
                          className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                          style={{ marginTop: "15px" }}
                        >
                          <label>Date Joined:</label>
                          <div
                            className="form-control text-white"
                            id="disabledInput"
                            style={{ background: "#dddddd" }}
                          >
                            <label>{this.state.dateJoined}</label>
                          </div>
                        </div>
                        <div
                          className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                          style={{ marginTop: "15px" }}
                        >
                          <label>Experience:</label>
                          <div
                            className="form-control text-white"
                            id="disabledInput"
                            style={{ background: "#dddddd" }}
                          >
                            <label>{this.state.experience}</label>
                          </div>
                        </div>
                        <div
                          className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                          style={{ marginTop: "15px" }}
                        >
                          <label>Office Location:</label>
                          <div
                            className="form-control text-white"
                            id="disabledInput"
                            style={{ background: "#dddddd" }}
                          >
                            <label>{this.state.officeLocation}</label>
                          </div>
                        </div>

                        <div
                          className="row justify-content-md-center"
                          style={{ marginTop: "20px" }}
                        >
                          <div className="col text-center"></div>
                          <button
                            className="btn btn-success"
                            onClick={this.updateUser}
                          >
                            Save Changes
                          </button>
                          <button
                            className="btn btn-danger"
                            onClick={this.cancel.bind(this)}
                            style={{ marginLeft: "10px" }}
                          >
                            Cancel
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
                {this.state.message && (
                  <div className="form-group">
                    <div
                      className={
                        this.state.successful
                          ? "alert alert-success"
                          : "alert alert-danger"
                      }
                      role="alert"
                    >
                      {this.state.message}
                    </div>
                  </div>
                )}
                <CheckButton
                  style={{ display: "none" }}
                  ref={(c) => {
                    this.checkBtn = c;
                  }}
                />
              </Form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default UserUpdateComponent;
