import React, { Component } from "react";
import UserService from "../services/UserService";
import saveTime from "../assets/saveTime.png";
import selfService from "../assets/selfService.png";
import onlineStudy from "../assets/onlineStudy.png";

export default class HomeComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: "",
    };
  }

  componentDidMount() {
    UserService.getPublicContent().then(
      (response) => {
        this.setState({
          content: response.data,
        });
      },
      (error) => {
        this.setState({
          content:
            (error.response && error.response.data) ||
            error.message ||
            error.toString(),
        });
      }
    );
  }

  render() {
    return (
      <div className="container">
        <div className=" jumbotron" style={{marginTop:"50px"}}>
          <div className="row justify-content-center">
            <homeH1>WELCOME TO KINDERGARTEN MANAGEMENT SYSTEM</homeH1>
          </div>
          <div className="row" style={{marginTop:"20px"}}>
            <div className="row justify-content-center">
              <div style={{ width: "22rem" }}>
                <div className="align-items-center text-dark">
                  <img
                    src={saveTime}
                    className="rounded-circle mx-auto d-block"
                    width="130"
                    height="130"
                    alt=""
                  />
                  <div className="card-body text-center">
                    <p className="card-title">
                      <strong>Rescue more time</strong>
                    </p>
                    <small>
                      Save substantial amount of time with our kindergarten management system
                    </small>
                  </div>
                </div>
              </div>

              <div style={{ width: "22rem", height: "23rem" }}>
                <div className="align-items-center text-dark">
                  <img
                    className=" rounded-circle mx-auto d-block"
                    src={onlineStudy}
                    width="130"
                    height="130"
                    alt=""
                  />
                  <div className="card-body text-center">
                    <p className="card-title">
                      <strong>Timely feedback</strong>
                    </p>
                    <small>
                      Automated mechanism of feedback leads to on-time processes
                    </small>
                  </div>
                </div>
              </div>

              <div style={{ width: "22rem", height: "23rem" }}>
                <div className="align-items-center text-dark">
                  <img
                    className=" rounded-circle mx-auto d-block"
                    src={selfService}
                    width="130"
                    height="130"
                    alt=""
                  />
                  <div className="card-body text-center">
                    <p className="card-title">
                      <strong>User self-service</strong>
                    </p>
                    <small>
                      Let your user service themselves to save more time for registration
                    </small>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
