import React, { Component } from "react";
import SkillsetService from "../services/skillset-service";

class CreateSkillset extends Component {
  constructor(props) {
    super(props);

    this.state = {
      skill: "",
      category: "",
      yearsOfExperience: "",
      proficiencyLevel: "",
    };
    this.changeSkillHandler = this.changeSkillHandler.bind(this);
    this.changeCategoryHandler = this.changeCategoryHandler.bind(this);
    this.changeYearsOfExperienceHandler = this.changeYearsOfExperienceHandler.bind(
      this
    );
    this.changeProficiencyLevelHandler = this.changeProficiencyLevelHandler.bind(
      this
    );
    this.saveSkillset = this.saveSkillset.bind(this);
  }

  saveSkillset = (e) => {
    e.preventDefault();
    let skillset = {
      skill: this.state.skill,
      category: this.state.category,
      yearsOfExperience: this.state.yearsOfExperience,
      proficiencyLevel: this.state.proficiencyLevel,
    };

    console.log("skillset => " + JSON.stringify(skillset));

    SkillsetService.addSkillset(skillset).then((res) => {
      this.props.history.push("/add-skillset");
    });
  };

  changeSkillHandler = (event) => {
    this.setState({ skill: event.target.value });
  };

  changeCategoryHandler = (event) => {
    this.setState({ category: event.target.value });
  };

  changeYearsOfExperienceHandler = (event) => {
    this.setState({ yearsOfExperience: event.target.value });
  };

  changeProficiencyLevelHandler = (event) => {
    this.setState({ proficiencyLevel: event.target.value });
  };

  cancel() {
    this.props.history.push("/hr/employee-list");
  }

  render() {
    return (
      <div>
        <h2 className="text-center">Skillset Adding Form</h2>
        <div className="container">
          <div className="row">
            <div className="card col-md-6 offset-md-3 offset-md-3">
              <div className="card-body">
                <form>
                  <div className="form-group">
                    <label> Skill: </label>
                    <input
                      placeholder="Skill"
                      name="skill"
                      className="form-control"
                      value={this.state.skill}
                      onChange={this.changeSkillHandler}
                    />
                  </div>
                  <div className="form-row">
                    <div className="form-group col-md-6">
                      <label> Category: </label>
                      <input
                        placeholder="Category"
                        name="category"
                        className="form-control"
                        value={this.state.category}
                        onChange={this.changeCategoryHandler}
                      />
                    </div>
                    <div className="form-group col-md-6">
                      <label> Years Of Experience: </label>
                      <input
                        placeholder="Years Of Experience"
                        name="yearsOfExperience"
                        className="form-control"
                        value={this.state.yearsOfExperience}
                        onChange={this.changeYearsOfExperienceHandler}
                      />
                    </div>
                  </div>
                  <div className="form-group">
                    <label> Proficiency Level: </label>
                    <input
                      placeholder="Proficiency Level"
                      name="proficiencyLevel"
                      className="form-control"
                      value={this.state.proficiencyLevel}
                      onChange={this.changeProficiencyLevelHandler}
                    />
                  </div>

                  <button
                    className="btn btn-success"
                    onClick={this.saveSkillset}
                  >
                    Save
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={this.cancel.bind(this)}
                    style={{ marginLeft: "10px" }}
                  >
                    Cancel
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default CreateSkillset;
