import React, { Component } from "react";
import UserService from "../services/user-service";

class CreateUserComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      employeeId: "",
      firstName: "",
      lastName: "",
      emailId: "",
      mailingAddress: "",
      dateOfBirth: "",
      department: "",
      position: "",
      reportingSupervisor: "",
      dateJoined: "",
      experience: "",
      contact: "",
      officeLocation: "",
    };
    this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
    this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
    this.saveEmployee = this.saveEmployee.bind(this);
  }

  saveEmployee = (e) => {
    e.preventDefault();
    let employee = {
      employeeId: this.state.employeeId,
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      emailId: this.state.emailId,
      mailingAddress: this.state.mailingAddress,
      dateOfBirth: this.state.dateOfBirth,
      department: this.state.department,
      position: this.state.position,
      reportingSupervisor: this.state.reportingSupervisor,
      dateJoined: this.state.dateJoined,
      experience: this.state.experience,
      contact: this.state.contact,
      officeLocation: this.state.officeLocation,
    };

    console.log("employee => " + JSON.stringify(employee));

    UserService.createEmployee(employee).then((res) => {
      this.props.history.push("/hr");
    });
  };

  changeEmployeeIdHandler = (event) => {
    this.setState({ employeeId: event.target.value });
  };

  changeFirstNameHandler = (event) => {
    this.setState({ firstName: event.target.value });
  };

  changeLastNameHandler = (event) => {
    this.setState({ lastName: event.target.value });
  };

  changeEmailHandler = (event) => {
    this.setState({ emailId: event.target.value });
  };

  changeAddressHandler = (event) => {
    this.setState({ mailingAddress: event.target.value });
  };

  changeDOBHandler = (event) => {
    this.setState({ dateOfBirth: event.target.value });
  };

  changeDepartmentHandler = (event) => {
    this.setState({ department: event.target.value });
  };

  changePositionHandler = (event) => {
    this.setState({ position: event.target.value });
  };

  changeReportingSupervisorHandler = (event) => {
    this.setState({ reportingSupervisor: event.target.value });
  };

  changeDateJoinedHandler = (event) => {
    this.setState({ dateJoined: event.target.value });
  };

  changeExperienceHandler = (event) => {
    this.setState({ experience: event.target.value });
  };

  changeContactHandler = (event) => {
    this.setState({ contact: event.target.value });
  };

  changeOfficeLocationHandler = (event) => {
    this.setState({ officeLocation: event.target.value });
  };

  cancel() {
    this.props.history.push("/hr/employee-list");
  }

  render() {
    return (
      <div>
        <h2 className="text-center">Employee Adding Form</h2>
        <div className="container">
          <div className="row">
            <div className="card col-md-6 offset-md-3 offset-md-3">
              <div className="card-body">
                <form>
                  <div className="form-group">
                    <label> Employee ID: </label>
                    <input
                      placeholder="Employee ID"
                      name="employeeId"
                      className="form-control"
                      value={this.state.employeeId}
                      onChange={this.changeEmployeeIdHandler}
                    />
                  </div>
                  <div className="form-row">
                    <div className="form-group col-md-6">
                      <label> First Name: </label>
                      <input
                        placeholder="First Name"
                        name="firstName"
                        className="form-control"
                        value={this.state.firstName}
                        onChange={this.changeFirstNameHandler}
                      />
                    </div>
                    <div className="form-group col-md-6">
                      <label> Last Name: </label>
                      <input
                        placeholder="Last Name"
                        name="lastName"
                        className="form-control"
                        value={this.state.lastName}
                        onChange={this.changeLastNameHandler}
                      />
                    </div>
                  </div>
                  <div className="form-group">
                    <label> Email: </label>
                    <input
                      placeholder="Email Address"
                      name="emailId"
                      className="form-control"
                      value={this.state.emailId}
                      onChange={this.changeEmailHandler}
                    />
                  </div>
                  <div className="form-group">
                    <label> Mailing Address: </label>
                    <textarea
                      placeholder="Mailing Address"
                      rows="4"
                      name="mailing addreess"
                      className="form-control"
                      value={this.state.mailingAddress}
                      onChange={this.changeAddressHandler}
                    />
                  </div>
                  <div className="form-group">
                    <label> Date Of Birth: </label>
                    <input
                      placeholder="dd/mm/yyyy"
                      name="dateofbirth"
                      className="form-control"
                      value={this.state.dateOfBirth}
                      onChange={this.changeDOBHandler}
                    />
                  </div>

                  <div className="form-group">
                    <label> Employee Department: </label>
                    <input
                      placeholder="Employee Department"
                      name="employeeDepartment"
                      className="form-control"
                      value={this.state.department}
                      onChange={this.changeDepartmentHandler}
                    />
                  </div>
                  <div className="form-group">
                    <label> Position: </label>
                    <input
                      placeholder="Position"
                      name="position"
                      className="form-control"
                      value={this.state.position}
                      onChange={this.changePositionHandler}
                    />
                  </div>

                  <div className="form-row">
                    <div className="form-group col-md-6">
                      <label> Reporting Supervisor: </label>
                      <input
                        placeholder="Reporting Supervisor"
                        name="reportingSupervisor"
                        className="form-control"
                        value={this.state.reportingSupervisor}
                        onChange={this.changeReportingSupervisorHandler}
                      />
                    </div>
                    <div className="form-group col-md-6">
                      <label> Date Joined: </label>
                      <input
                        placeholder="dd/mm/yyyy"
                        name="dateJoined"
                        className="form-control"
                        value={this.state.dateJoined}
                        onChange={this.changeDateJoinedHandler}
                      />
                    </div>
                  </div>
                  <div className="form-row">
                    <div className="form-group col-md-6">
                      <label> Experience (Years): </label>
                      <input
                        placeholder="Experience (Years)"
                        name="experience"
                        className="form-control"
                        value={this.state.experience}
                        onChange={this.changeExperienceHandler}
                      />
                    </div>
                    <div className="form-group col-md-6">
                      <label> Contact: </label>
                      <input
                        placeholder="Contact (without '-')"
                        name="contact"
                        className="form-control"
                        value={this.state.contact}
                        onChange={this.changeContactHandler}
                      />
                    </div>
                  </div>
                  <div className="form-group">
                    <label> Office Location: </label>
                    <textarea
                      placeholder="Office Location"
                      rows="4"
                      name="officeLocation"
                      className="form-control"
                      value={this.state.officeLocation}
                      onChange={this.changeOfficeLocationHandler}
                    />
                  </div>
                  <button
                    className="btn btn-success"
                    onClick={this.saveEmployee}
                  >
                    Save
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={this.cancel.bind(this)}
                    style={{ marginLeft: "10px" }}
                  >
                    Cancel
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default CreateUserComponent;
