    const uploadImage = (e) => {
      const image = e.target.files[0];
      const uploadTask = storage.ref(`images/${image.name}`).put(image);
      uploadTask.on(
        "state_changed",
        (snapshot) => {
          // progress function ....
          const progress = Math.round(
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100
          );
          this.setState({ progress });
        },
        (error) => {
          // error function ....
          console.log(error);
        },
        () => {
          // complete function ....
          storage
            .ref("images")
            .child(image.name)
            .getDownloadURL()
            .then((url) => {
              console.log(url);
              this.setState({ profilePicture: url });
            });
        }
      );
    };

    const uploadAttachment = (e) => {
      if (this.state.userId === "") {
        alert("Wrong ID");
        return;
      } else {
        console.log("file length: ", e.target.files.length);

        for (let i = 0; i < e.target.files.length; i++) {
          const file = e.target.files[i];
          try {
            if (file.size > 5242880) {
              alert("File size is too big! Please choose another file.");
            } else {
              const storageRef = storage.ref(
                `attachments/` + this.state.userId
              );
              const fileRef = storageRef.child(file.name);

              fileRef.put(file).then(() => {
                console.log("Uploaded " + file.name + " successfully");
              });
            }
          } catch {
            return;
          }
        }
        try {
          setTimeout(() => {
            const storageRef = storage.ref(
              `attachments/` + this.state.userId
            );
            storageRef
              .listAll()
              .then((res) => {
                res.items.forEach((itemRef) => {
                  itemRef
                    .getDownloadURL()
                    .then((url) => {
                      var attachmentList = this.state.fileList.concat(url);
                      this.setState({ fileList: attachmentList });
                      this.setState({
                        attachment: this.state.fileList.join(","),
                      });
                      console.log(this.state.fileList);
                      console.log(this.state.attachment);
                    })
                    .catch(function (error) {
                      console.log(error);
                    });
                }, 2000);
