import React, { Component } from "react";
import UserService from "../services/UserService";
import AuthService from "../services/AuthService";

import ReactHover from "react-hover";
import { Trigger, Hover } from "react-hover/dist/ReactHover";

class BoardStudentComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: this.props.match.params.id,
      user: [],
      ID: "",
      userReady: false,
      selectedFile: null,
    };
    this.userUpdate = this.userUpdate.bind(this);
    this.getAttachment = this.getAttachment.bind(this);
  }

  componentDidMount() {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) this.setState({ redirect: "/login" });
    this.setState({ currentUser: currentUser, userReady: false });

    UserService.getUserBoard(currentUser.id).then((res) => {
      this.setState({ employee: res.data });
      console.log("user: ", this.state.user.id);
    });
  }

  userUpdate(id) {
    this.props.history.push(`/user-update/${id}`);
  }

  getAttachment() {
    const filePreviewOption = {
      followCursor: false,
      shiftX: 20,
      shiftY: 0,
    };

    if (this.state.user.id === "add") {
      return;
    } else {
      if (!this.state.user.attachment) {
        return;
      } else {
        const downloadLink = [];
        for (const [index, value] of this.state.user.attachment
          .split(",")
          .entries()) {
          const initial = value
            .split(RegExp("%2..*%2F(.*?)alt"))[1]
            .split(".")[0];
          const fileName = initial.replaceAll("%20", " ");
          //downloadLink.push(<li key={index}><a href={value}>{fileName</a></li>)
          downloadLink.push(
            <ReactHover options={filePreviewOption}>
              <Trigger type="trigger">
                <ul className="list-group list-group-flush">
                  <li
                    className="list-group-item"
                    style={{ marginBottom: "5px" }}
                    key={index}
                  >
                    <i className="fa fa-download"></i>{" "}
                    <a href={value}>{fileName}</a>
                  </li>
                </ul>
              </Trigger>
              <Hover type="hover">
                <div>
                  <iframe
                    src={value}
                    title="File Preview"
                    style={{ width: "500px", height: "300px" }}
                  />
                </div>
              </Hover>
            </ReactHover>
          );
        }
        return downloadLink;
      }
    }
  }

  render() {
    const { currentUser } = this.state;

    return (
      <div
        className="container"
        style={{ marginTop: "20px", marginBottom: "20px" }}
      >
        <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12 text-center">
          <h2 className="text">{this.state.user.name}'s Profile</h2>
        </div>
        <div className="row gutters" style={{ marginTop: "20px" }}>
          <div className="col-xl-3 col-lg-3 col-md-12 col-sm-12 col-12">
            <div className="card h-100">
              <div className="card-body text-center">
                <div className="view-account">
                  <div className="view">
                    <div className="image" style={{ padding: "10px" }}>
                      <img
                        src={
                          this.state.user.profilePicture ||
                          "http://via.placeholder.com/150x150"
                        }
                        alt="Profile_Picture"
                        height="150"
                        width="150"
                        className=" rounded-circle mx-auto d-block"
                      />
                    </div>
                    <h5 className="name">
                      {this.state.user.name}
                    </h5>
                    <div className="card-header">List of Files</div>
                    {this.getAttachment()}
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="col-xl-9 col-lg-9 col-md-12 col-sm-12 col-12">
            <div className="card h-100">
              <div className="card-body">
                <div className="row gutters">
                  <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
                    <h6 className="mb-3 text-primary">Personal Details</h6>
                  </div>
                  <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                    <label>First Name:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.firstName}</label>
                    </div>
                  </div>
                  <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                    <label>Last Name:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.lastName}</label>
                    </div>
                  </div>
                  <div
                    className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                    style={{ marginTop: "15px" }}
                  >
                    <label>Date of Birth:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.dateOfBirth}</label>
                    </div>
                  </div>
                  <div
                    className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                    style={{ marginTop: "15px" }}
                  >
                    <label>Personal Email:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.personalEmail}</label>
                    </div>
                  </div>
                  <div
                    className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                    style={{ marginTop: "15px" }}
                  >
                    <label>Contact Number:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.contact}</label>
                    </div>
                  </div>
                  <div
                    className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                    style={{ marginTop: "15px" }}
                  >
                    <label>Mailing Address:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.mailingAddress}</label>
                    </div>
                  </div>
                </div>
                <div className="row gutters" style={{ marginTop: "30px" }}>
                  <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
                    <h6 className="mb-3 text-primary">Employment Details</h6>
                  </div>
                  <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                    <label>Employee ID:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.employeeId}</label>
                    </div>
                  </div>
                  <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12">
                    <label>Corporate Email:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.email}</label>
                    </div>
                  </div>

                  <div
                    className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                    style={{ marginTop: "15px" }}
                  >
                    <label>Department:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.department}</label>
                    </div>
                  </div>
                  <div
                    className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                    style={{ marginTop: "15px" }}
                  >
                    <label>Position:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.position}</label>
                    </div>
                  </div>
                  <div
                    className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                    style={{ marginTop: "15px" }}
                  >
                    <label>Reporting Supervisor:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.reportingSupervisor}</label>
                    </div>
                  </div>
                  <div
                    className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                    style={{ marginTop: "15px" }}
                  >
                    <label>Date Joined:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.dateJoined}</label>
                    </div>
                  </div>
                  <div
                    className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                    style={{ marginTop: "15px" }}
                  >
                    <label>Experience:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.experience}</label>
                    </div>
                  </div>
                  <div
                    className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-12"
                    style={{ marginTop: "15px" }}
                  >
                    <label>Office Location:</label>
                    <div className="form-control" id="disabledInput">
                      <label>{this.state.employee.officeLocation}</label>
                    </div>
                  </div>

                  <div className="row justify-content-md-center">
                    <div className="col text-center"></div>
                    <button
                      className="btn btn-info"
                      style={{ marginTop: "20px" }}
                      onClick={() => this.userUpdate(currentUser.id)}
                    >
                      Update Details
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default BoardStudentComponent;
