import React, { Component } from "react";
import AddSkillset from "./skillset-create";

class Skillset extends Component {
  constructor(props) {
    super(props);

    this.state = {
      skillset: [
        {
          index: Math.random(),
          skill: "",
          category: "",
          yearsOfExperience: "",
          proficiencyLevel: "",
        },
      ],
    };
  }

  handleChange = (e) => {
    if (
      ["skill", "category", "yearsOfExperience", "proficiencyLevel"].includes(
        e.target.name
      )
    ) {
      let skillset = [...this.state.skillset];
      skillset[e.target.dataset.id][e.target.name] = e.target.value;
    } else {
      this.setState({ [e.target.name]: e.target.value });
    }
  };

  addNewRow = (e) => {
    this.setState((prevState) => ({
      skillset: [
        ...prevState.skillset,
        {
          index: Math.random(),
          skill: "",
          category: "",
          yearsOfExperience: "",
          proficiencyLevel: "",
        },
      ],
    }));
  };

  deteteRow = (index) => {
    this.setState({
      skillset: this.state.skillset.filter((s, sindex) => index !== sindex),
    });
  };

  clickOnDelete(record) {
    this.setState({
      skillset: this.state.skillset.filter((r) => r !== record),
    });
  }

  render() {
    let { skillset } = this.state;
    return (
      <div className="content">
        <form onSubmit={this.handleSubmit} onChange={this.handleChange}>
          <div className="row">
            <div className="col-sm-1" />
            <div className="col-sm-10">
              <h2 className="text-center">Skillset Details</h2>
              <div className="container">
                <div className="row" style={{ marginTop: 20 }}>
                  <AddSkillset
                    add={this.addNewRow}
                    delete={this.clickOnDelete.bind(this)}
                    skillDetails={skillset}
                  />
                </div>
              </div>
            </div>
            <div className="col-sm-1" />
          </div>
        </form>
      </div>
    );
  }
}
export default Skillset;
