import React, { Component } from "react";
import UserService from "../services/user-service";

function searchingFor(ID) {
  return function (x) {
    return x.username !== null && x.username.includes(ID);
  };
}

class BoardHr extends Component {
  constructor(props) {
    super(props);

    this.state = {
      employee: [],
      ID: [],
    };
    //this.addEmployee = this.addEmployee.bind(this);
    this.editEmployee = this.editEmployee.bind(this);
    this.removeEmployee = this.removeEmployee.bind(this);
    this.searchHandler = this.searchHandler.bind(this);
    this.viewDeletedEmployee = this.viewDeletedEmployee.bind(this);
  }

  searchHandler(event) {
    this.setState({ ID: event.target.value });
  }

  clearFilter = (event) => {
    Array.from(document.querySelectorAll("input")).forEach(
      (input) => (input.value = "")
    );
    this.setState({
      ID: event.target.value,
    });
  };

  componentDidMount() {
    UserService.getHrBoard().then((res) => {
      this.setState({ employee: res.data });
    });
  }

  addEmployee() {
    this.props.history.push("/add-employee");
  }

  editEmployee(id) {
    this.props.history.push(`/update-employee/${id}`);
  }

  removeEmployee(id) {
    UserService.removeEmployee(id).then((res) => {
      this.setState({
        employee: this.state.employee.filter((employee) => employee.id !== id),
      });
    });
  }

  viewEmployee(id) {
    this.props.history.push(`/view-employee/${id}`);
  }

  viewDeletedEmployee() {
    this.props.history.push(`/view-deleted-employee`);
  }

  render() {
    const filteredEmployees = this.state.employee.filter((deletedEmployee) => {
      return deletedEmployee.deleted === false;
    });

    return (
      <div>
        <h2 className="text-center">Employee List</h2>
        <div className="d-flex flex-row">
          {/* <button
              className="btn btn-outline-secondary"
              onClick={this.addEmployee}
            >
              <i className="fa fa-plus"></i> Add Employee
            </button> */}
          <div className="col-sm form-group has-search">
            <span className="fa fa-search form-control-feedback"></span>
            <input
              type="text"
              className="form-control"
              placeholder="Employee Username"
              onChange={this.searchHandler}
            />
          </div>
          <div className="col-sm-6">
            <button
              className="btn btn-outline-secondary"
              onClick={this.clearFilter}
            >
              Clear <i className="fa fa-times"></i>
            </button>
          </div>

          <div className="">
            <button
              className="btn btn-outline-secondary"
              onClick={this.viewDeletedEmployee}
            >
              View Deleted Employee
            </button>
          </div>
        </div>

        <div className="row justify-content-center">
          {filteredEmployees
            .filter(searchingFor(this.state.ID))
            .map((employee) => (
              <div key={employee.id}>
                <div className="col">
                  <div
                    className="card"
                    style={{ width: "15rem", height: "22rem" }}
                  >
                    <div className="align-items-center text-dark">
                      <img
                        className=" rounded-circle mx-auto d-block"
                        src={
                          employee.profilePicture ||
                          "//ssl.gstatic.com/accounts/ui/avatar_2x.png"
                        }
                        width="90"
                        height="90"
                        alt=""
                      />
                      <div className="card-body text-center">
                        <h5 className="card-title">
                          {employee.firstName} {employee.lastName}
                        </h5>
                        <small className="">{employee.position}</small>
                        {/* <small className="card-text text-center">
                          {employee.position}
                          <span> </span>
                          <i class="fa fa-briefcase"></i>
                        </small> */}

                        <div className="">
                          <hr />
                        </div>

                        {/* <div
                          className="row"
                          style={{ height: "25px", marginLeft: "1px" }}
                        >
                          <p className="text-muted">
                            <i className="fa fa-briefcase"></i>
                            <span> </span>
                            {employee.position}
                          </p>
                        </div> */}
                        <div
                          className="row"
                          style={{ height: "25px", marginLeft: "1px" }}
                        >
                          <p className="text-muted">
                            <i className="fa fa-building"></i>
                            <span> </span>
                            {employee.department}
                          </p>
                        </div>
                        <div
                          className="row"
                          style={{ height: "25px", marginLeft: "1px" }}
                        >
                          <p className="text-muted">
                            <i className="fa fa-phone"></i>
                            <span> </span>
                            {employee.contact}
                          </p>
                        </div>

                        <div className="">
                          <hr />
                        </div>

                        <div className="row justify-content-center">
                          <button
                            style={{ marginLeft: "10px" }}
                            onClick={() => this.viewEmployee(employee.id)}
                            className="btn btn-outline-primary"
                            data-toggle="tooltip"
                            data-placement="top"
                            title="View more"
                          >
                            <i className="fa fa-eye"></i>
                          </button>
                          <button
                            style={{ marginLeft: "10px" }}
                            onClick={() => this.editEmployee(employee.id)}
                            className="btn btn-outline-info"
                            data-toggle="tooltip"
                            data-placement="top"
                            title="Update profile"
                          >
                            <i className="fa fa-edit"></i>
                          </button>
                          <button
                            style={{
                              marginLeft: "10px",
                            }}
                            className="btn btn-outline-danger"
                            data-toggle="tooltip"
                            data-placement="top"
                            title="Remove employee"
                            onClick={() => {
                              if (window.confirm("Remove the employee?")) {
                                this.removeEmployee(employee.id);
                              }
                            }}
                          >
                            <i className="fa fa-trash"></i>
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
        </div>
      </div>
    );
  }
}

export default BoardHr;
