import React, { Component } from "react";
import UserService from "../services/UserService";
import Table from "react-bootstrap/Table";

class ViewDeletedUser extends Component {
  constructor(props) {
    super(props);

    this.state = {
      id: this.props.match.params.id,
      user: [],
      ID: [],
    };
    this.restoreDeletedUser = this.restoreDeletedUser.bind(this);
    this.deleteUser = this.deleteUser.bind(this);
  }

  componentDidMount() {
    UserService.getPrincipalBoard().then((res) => {
      this.setState({ user: res.data });
    });
  }

  restoreDeletedUser(id) {
    UserService.restoreDeletedUser(id).then((res) => {
      this.setState({
        user: this.state.user.filter((user) => user.id !== id),
      });
    });
  }

  deleteUser(id) {
    UserService.deleteUser(id).then((res) => {
      this.setState({
        user: this.state.user.filter((user) => user.id !== id),
      });
    });
  }

  cancel() {
    this.props.history.push("/principal/student-list");
  }

  render() {
    const filteredEmployees = this.state.employee.filter((deletedEmployee) => {
      return deletedEmployee.deleted === true;
    });

    return (
      <div>
        <h2 className="text-center">Deleted Employee List</h2>
        <div className="d-flex flex-row">
          <div className="col-sm-8">
            <button
              className="btn btn-outline-secondary"
              onClick={this.cancel.bind(this)}
              //style={{ marginLeft: "15px" }}
            >
              <i className="fa fa-chevron-left"></i> Go back to Employee List
            </button>
          </div>
        </div>

        <Table responsive striped bordered hover size="sm">
          <thead className="">
            <tr>
              <th scope="col" className="text-center">
                First Name
              </th>
              <th scope="col" className="text-center">
                Last Name
              </th>
              <th scope="col" className="text-center">
                Username
              </th>
              <th scope="col" className="text-center">
                Department
              </th>
              <th scope="col" className="text-center">
                Action
              </th>
            </tr>
          </thead>
          {filteredEmployees.map((employee) => (
            <tbody>
              <tr key={employee.id}>
                <td style={{ padding: "10px" }}>{employee.firstName}</td>
                <td style={{ padding: "10px" }}>{employee.lastName}</td>
                <td style={{ padding: "10px" }}>{employee.username}</td>
                <td style={{ padding: "10px" }}>{employee.department}</td>
                <td className="text-center">
                  <button
                    style={{
                      marginLeft: "10px",
                      marginRight: "10px",
                    }}
                    className="btn btn-success"
                    data-toggle="tooltip"
                    data-placement="top"
                    title="Restore employee"
                    onClick={() => {
                      if (window.confirm("Restore the employee?")) {
                        this.restoreDeletedEmployee(employee.id);
                      }
                    }}
                  >
                    Restore
                  </button>
                  <button
                    style={{
                      marginLeft: "10px",
                      marginRight: "10px",
                    }}
                    className="btn btn-danger"
                    data-toggle="tooltip"
                    data-placement="top"
                    title="Delete employee"
                    onClick={() => {
                      if (window.confirm("Permanently delete the employee?")) {
                        this.deleteEmployee(employee.id);
                      }
                    }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            </tbody>
          ))}
        </Table>
      </div>
    );
  }
}

export default ViewDeletedUser;
