import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import logo from "./assets/TeamC.png";
import AuthService from "./services/auth-service";

import Home from "./components/home-component";
import FooterComponent from "./components/footer-component";
import Login from "./components/login-component";
import Register from "./components/register-component";
import Profile from "./components/profile-component";
import BoardStudentComponent from "./components/BoardStudentComponent";
import BoardPrincipalComponent from "./components/BoardPrincipalComponent";
import BoardAdmin from "./components/board-admin-component";
import CreateUserComponent from "./components/CreateUserComponent";
import UpdateUserComponent from "./components/UpdateUserComponent";
import ViewUserComponent from "./components/ViewUserComponent";
import UserUpdateComponent from "./components/user-update-component";
import ViewDeletedUser from "./components/ViewDeletedUser";
import ForgotPassword from "./components/forgot-password-form";
import ResetPassword from "./components/reset-password-form";
import ChangePassword from "./components/profile-change-password";

class App extends Component {
  constructor(props) {
    super(props);
    this.logOut = this.logOut.bind(this);

    this.state = {
      showStudentBoard: false,
      showPrincipalBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    };
  }

  componentDidMount() {
    const user = AuthService.getCurrentUser();

    if (user) {
      this.setState({
        currentUser: user,
        showStudentBoard: user.roles.includes("ROLE_STUDENT"),
        showPrincipalBoard: user.roles.includes("ROLE_PRINCIPAL"),
        showAdminBoard: user.roles.includes("ROLE_ADMIN"),
      });
    }
  }

  logOut() {
    AuthService.logout();
  }

  render() {
    const {
      currentUser,
      showStudentBoard,
      showPrincipalBoard,
      showAdminBoard,
    } = this.state;

    return (
      <div className="main-app-container">
        <Router>
          <nav className="navbar navbar-expand navbar-dark bg-dark">
            <Link to={"/home"} className="navbar-brand">
              <img src={logo} width="25" height="25" alt="" />
              <span> </span>
              <strong>Kindergarten Management System</strong>
            </Link>

            <div className="navbar-nav mr-auto">
              {showPrincipalBoard && (
                <div>
                  <li className="nav-item">
                    <Link to={"/principal/student-list"} className="nav-link">
                      Principal Board <i className="fa fa-clipboard"></i>
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link to={"/register"} className="nav-link">
                      Register New Account <i className="fa fa-user-plus"></i>
                    </Link>
                  </li>
                </div>
              )}

              {showAdminBoard && (
                <li className="nav-item">
                  <Link to={"/admin"} className="nav-link">
                    Admin Board <i className="fa fa-clipboard"></i>
                  </Link>
                </li>
              )}

              {showStudentBoard && (
                <div>
                  <li className="nav-item">
                    <Link to={"/user"} className="nav-link">
                      Student Board <i className="fa fa-clipboard"></i>
                    </Link>
                  </li>
                </div>
              )}
            </div>

            {currentUser ? (
              <div className="navbar-nav ml-auto">
                <li className="nav-item">
                  <Link to={"/profile"} className="nav-link">
                    {currentUser.username} <i className="fa fa-user"></i>
                  </Link>
                </li>
                <li className="nav-item">
                  <a href="/login" className="nav-link" onClick={this.logOut}>
                    Log Out <i className="fa fa-sign-out"></i>
                  </a>
                </li>
              </div>
            ) : (
              <div className="navbar-nav ml-auto">
                <li className="nav-item">
                  <Link to={"/login"} className="nav-link">
                    Login <i className="fa fa-sign-in"></i>
                  </Link>
                </li>
              </div>
            )}
          </nav>

          <div className="container mt-3">
            <Switch>
              <Route exact path={["/", "/home"]} component={Home} />
              <Route exact path="/login" component={Login} />
              <Route exact path="/register" component={Register} />
              <Route exact path="/profile" component={Profile} />
              <Route path="/user" component={BoardStudentComponent} />
              <Route path="/principal/student-list" component={BoardPrincipalComponent}></Route>
              <Route path="/admin" component={BoardAdmin} />

              <Route
                path="/user-update/:id"
                component={UserUpdateComponent}
              ></Route>

              <Route
                path="/add-user"
                component={CreateUserComponent}
              ></Route>

              <Route
                path="/view-user/:id"
                component={ViewUserComponent}
              ></Route>

              <Route
                path="/update-user/:id"
                component={UpdateUserComponent}
              ></Route>

              <Route
                path="/view-deleted-user"
                component={ViewDeletedUser}
              ></Route>

              <Route path="/forgot_password" component={ForgotPassword}></Route>

              <Route
                path="/reset_password/:token"
                component={ResetPassword}
              ></Route>

              <Route path="/change_password" component={ChangePassword}></Route>
            </Switch>
          </div>
          <FooterComponent />
        </Router>
      </div>
    );
  }
}

export default App;
