import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

import graduation_cap from "./assets/graduation_cap.png";

import AuthService from "./services/AuthService";
import HomeComponent from "./components/HomeComponent";
import FooterComponent from "./components/FooterComponent";
import LoginComponent from "./components/LoginComponent";
import RegisterComponent from "./components/RegisterComponent";
import ProfileComponent from "./components/ProfileComponent";
import BoardStudentComponent from "./components/BoardStudentComponent";
import BoardPrincipalComponent from "./components/BoardPrincipalComponent";
import BoardAdminComponent from "./components/BoardAdminComponent";
import UpdateStudentComponent from "./components/UpdateStudentComponent";
import ViewUserComponent from "./components/ViewUserComponent";
import UserUpdateComponent from "./components/UserUpdateComponent";
import ViewDeletedUser from "./components/ViewDeletedUser";
import ForgotPasswordComponent from "./components/ForgotPasswordComponent";
import ResetPasswordComponent from "./components/ResetPasswordComponent";
import ChangePasswordComponent from "./components/ChangePasswordComponent";
import { NavDropdown } from "react-bootstrap";

class App extends Component {
  constructor(props) {
    super(props);
    this.logOut = this.logOut.bind(this);

    this.state = {
      showStudentBoard: false,
      showPrincipalBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    };
  }

  componentDidMount() {
    const user = AuthService.getCurrentUser();

    if (user) {
      this.setState({
        currentUser: user,
        showStudentBoard: user.roles.includes("ROLE_STUDENT"),
        showPrincipalBoard: user.roles.includes("ROLE_PRINCIPAL"),
        showAdminBoard: user.roles.includes("ROLE_ADMIN"),
      });
    }
  }

  logOut() {
    AuthService.logout();
  }

  render() {
    const {
      currentUser,
      showStudentBoard,
      showPrincipalBoard,
      showAdminBoard,
    } = this.state;

    return (
      <div className="main-app-container">
        <Router>
          <nav className="navbar navbar-expand navbar-dark">
            <Link to={"/home"} className="navbar-brand">
              <img src={graduation_cap} width="25" height="25" alt="" />
              <span> </span>
              <strong>Kindergarten Management System</strong>
            </Link>

            <div className="navbar-nav mr-auto">
              {showPrincipalBoard && (
                <div>
                  <li className="nav-item">
                    <Link to={"/principal/student-list"} className="nav-link">
                      Principal Board <i className="fa fa-clipboard"></i>
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link to={"/register"} className="nav-link">
                      Register New Account <i className="fa fa-user-plus"></i>
                    </Link>
                  </li>
                </div>
              )}

              {showAdminBoard && (
                <li className="nav-item">
                  <Link to={"/admin"} className="nav-link">
                    Admin Board <i className="fa fa-clipboard"></i>
                  </Link>
                </li>
              )}

              {showStudentBoard && (
                <div>
                  <li className="nav-item">
                    <Link to={"/user"} className="nav-link">
                      Student Board <i className="fa fa-clipboard"></i>
                    </Link>
                  </li>
                </div>
              )}
            </div>

            {currentUser ? (
              <div className="navbar-nav ml-auto">
                <li className="nav-item">
                  <NavDropdown title={currentUser.username} id="nav-dropdown">
                    <NavDropdown.Item link to={"/profile"}>{currentUser.username}</NavDropdown.Item>
                    <NavDropdown.Item to={"/login"} onClick={this.logOut}>Logout</NavDropdown.Item>
                  </NavDropdown>

                  <Link to={"/profile"} className="nav-link">
                    {currentUser.username} <i className="fa fa-user"></i>
                  </Link>
                </li>
                <li className="nav-item">
                  <a href="/login" className="nav-link" onClick={this.logOut}>
                    Log Out <i className="fa fa-sign-out"></i>
                  </a>
                </li>
              </div>
            ) : (
              <div className="navbar-nav ml-auto">
                <li className="nav-item">
                  <Link to={"/login"} className="nav-link">
                    Login <i className="fa fa-sign-in"></i>
                  </Link>
                </li>
              </div>
            )}
          </nav>

          <div className="container mt-3">
            <Switch>
              <Route exact path={["/", "/home"]} component={HomeComponent} />
              <Route exact path="/login" component={LoginComponent} />
              <Route exact path="/register" component={RegisterComponent} />
              <Route exact path="/profile" component={ProfileComponent} />
              <Route path="/user" component={BoardStudentComponent} />
              <Route path="/principal/student-list" component={BoardPrincipalComponent}></Route>
              <Route path="/admin" component={BoardAdminComponent} />

              <Route
                path="/user-update/:id"
                component={UserUpdateComponent}
              ></Route>

              <Route
                path="/view-user/:id"
                component={ViewUserComponent}
              ></Route>

              <Route
                path="/update-student/:id"
                component={UpdateStudentComponent}
              ></Route>

              <Route
                path="/view-deleted-user"
                component={ViewDeletedUser}
              ></Route>

              <Route path="/forgot_password" component={ForgotPasswordComponent}></Route>

              <Route
                path="/reset_password/:token"
                component={ResetPasswordComponent}
              ></Route>

              <Route path="/change_password" component={ChangePasswordComponent}></Route>
            </Switch>
          </div>
          <FooterComponent />
        </Router>
      </div>
    );
  }
}

export default App;
