import React, { Component } from "react";
import SkillsetService from "../services/skillset-service";

class SkillsetList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      skillset: [],
      value: "",
      //yearsOfExperience: "",
      //proficiencyLevel: "",
    };
    //this.addSkill = this.addSkill.bind(this);
  }

  componentDidMount() {
    SkillsetService.getAllSkillset().then((res) => {
      this.setState({ skillset: res.data });
    });
  }

  onChangeValue = (event) => {
    this.setState({ value: event.target.value });
  };

  onAddItem = () => {
    // not allowed AND not working
    this.setState((state) => {
      const skillset = state.skillset.push(state.value);

      return {
        skillset,
        value: "",
      };
    });
  };

  render() {
    return (
      <div>
        <h2 className="text-center">Skillset List</h2>
        {/* <div className="row">
          <table
            style={{ marginTop: "10px" }}
            className="table table-striped table-boardered"
          >
            <thead>
              <tr>
                <th>Skill</th>
                <th>Category</th>
              </tr>
            </thead>

            <tbody>
              {this.state.skillset.map((skillset) => (
                <tr key={skillset.id}>
                  <td>{skillset.skill}</td>

                  <td>{skillset.category}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div> */}
        <div>
          <ul>
            {this.state.skillset.map((skillset) => (
              <li key={skillset.name}></li>
            ))}
          </ul>

          <input
            type="text"
            value={this.state.value}
            onChange={this.onChangeValue}
          />
          <button
            type="button"
            onClick={this.onAddItem}
            disabled={!this.state.value}
          >
            Add
          </button>
        </div>
      </div>
    );
  }
}

export default SkillsetList;
